import {
  ApartmentOutlined,
  BuildOutlined,
  ContactsOutlined,
  DownOutlined,
  FullscreenExitOutlined,
  FullscreenOutlined,
  LoginOutlined,
  SettingOutlined,
  TeamOutlined,
} from "@ant-design/icons";
import { Dropdown, Image, Space, type MenuProps } from "antd";
import { useMenuStore, useTokensStore } from "@store/index";
import { useCallback, useEffect, useState } from "react";

import { Link, useLocation, useNavigate } from "react-router-dom";
interface SystemHeaderProps {}

const icon: any = {
  ContactsOutlined: <ContactsOutlined />,
  SettingOutlined: <SettingOutlined />,
  BuildOutlined: <BuildOutlined />,
  TeamOutlined: <TeamOutlined />,
  ApartmentOutlined: <ApartmentOutlined />,
};

const SystemHeader: React.FC<SystemHeaderProps> = () => {
  const { clearToken } = useTokensStore();
  const { menus } = useMenuStore();
  const navigate = useNavigate();
  const [fullscreen, setFullscreen] = useState(false);
  const { pathname } = useLocation();

  const items: MenuProps["items"] = [
    {
      key: "2",
      label: (
        <div
          onClick={() => {
            navigate("/login");
            clearToken();
          }}
        >
          退出登录
        </div>
      ),
      icon: <LoginOutlined />,
    },
  ];
  // 打开全屏
  const openFullscreen = () => {
    if (document.documentElement.requestFullscreen) {
      document.documentElement.requestFullscreen();
      setFullscreen(true);
    } else if (document.documentElement.mozRequestFullScreen) {
      /* Firefox */
      document.documentElement.mozRequestFullScreen();
      setFullscreen(true);
    } else if (document.documentElement.webkitRequestFullscreen) {
      /* Chrome, Safari & Opera */
      document.documentElement.webkitRequestFullscreen();
      setFullscreen(true);
    } else if (document.documentElement.msRequestFullscreen) {
      /* IE/Edge */
      document.documentElement.msRequestFullscreen();
      setFullscreen(true);
    }
  };
  // 退出全屏
  const closeFullscreen = () => {
    if (document.exitFullscreen) {
      document.exitFullscreen();
      setFullscreen(false);
    } else if (document.mozCancelFullScreen) {
      /* Firefox */
      document.mozCancelFullScreen();
      setFullscreen(false);
    } else if (document.webkitExitFullscreen) {
      /* Chrome, Safari and Opera */
      document.webkitExitFullscreen();
      setFullscreen(false);
    } else if (document.msExitFullscreen) {
      /* IE/Edge */
      document.msExitFullscreen();
      setFullscreen(false);
    }
  };
  const fullscreenchangeHandler = useCallback(() => {
    if (document.fullscreenElement) {
      setFullscreen(true);
    } else {
      setFullscreen(false);
    }
  }, []);

  useEffect(() => {
    document.addEventListener("fullscreenchange", fullscreenchangeHandler);
    return () => {
      document.removeEventListener("fullscreenchange", fullscreenchangeHandler);
    };
  }, []);
  return (
    <div className="flex flex-1 h-12 pl-3 pr-3 border-b border-gray-200 bg-black">
      <div className="flex-1 flex items-center w-52">
        <Link
          to="/home"
          className="flex items-center"
          style={{ width: "200px" }}
        >
          <Image
            width={30}
            height={30}
            src="error"
            preview={false}
            fallback="data:image/png;base64,"
          />
          <h1 className=" text-lg font-bold ml-2 text-white">讲者中心管理</h1>
        </Link>
        <ul className="flex">
          {menus.map((item) => (
            <li
              key={item.path}
              className={`cursor-pointer flex p-1 pl-2 pr-2 hover:bg-gray-100 rounded-md mr-2  ${
                pathname.includes(item.path as string)
                  ? "text-white"
                  : "text-gray-300"
              }`}
              style={{
                background: pathname.includes(item.path as string)
                  ? "#1677ff"
                  : "none",
              }}
              onClick={() => navigate(item.path as string)}
            >
              {item.icon ? icon?.[item.icon] : null}
              <span className="pl-1">{item.title}</span>
            </li>
          ))}
        </ul>
      </div>
      <div className="flex items-center">
        <div
          className="pr-2 cursor-pointer hover:text-white text-gray-300 font-bold"
          onClick={() => {
            fullscreen ? closeFullscreen() : openFullscreen();
          }}
        >
          {fullscreen ? <FullscreenExitOutlined /> : <FullscreenOutlined />}
        </div>
        <Image
          width={30}
          height={30}
          preview={false}
          src="error"
          fallback="data:image/png;base64,"
        />
        <Dropdown menu={{ items }}>
          <div className="pl-2 text-sm">
            <Space className="text-gray-300 cursor-pointer">
              myfirebug
              <DownOutlined />
            </Space>
          </div>
        </Dropdown>
      </div>
    </div>
  );
};

export default SystemHeader;
